INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1, 1, 0, 'âœ¨ Indian Astrology, or Jyotish, means "science of light," illuminating your life''s path and hidden potential. It''s truly a cosmic GPS!', '2025-11-18 20:28:49.526813 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2, 1, 1, 'ğŸŒ™ Unlike Western astrology, Jyotish heavily emphasizes your Moon sign (Rashi) over your Sun sign, believing it reflects your mind and emotions more deeply.', '2025-11-18 20:28:49.526821 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (3, 1, 2, 'ğŸŒŒ It uses a fixed, sidereal zodiac (Nirayana) that aligns with the actual constellations, which is different from the seasonally based tropical zodiac.', '2025-11-18 20:28:49.526823 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (4, 1, 3, 'ğŸª Jyotish recognizes 9 planets, or "Navagrahas," including the two mysterious shadow planets, Rahu and Ketu, which are lunar nodes and powerful karmic indicators.', '2025-11-18 20:28:49.526824 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (5, 1, 4, 'â³ One of its coolest features is the "Dasha" system, a unique planetary period predictor that forecasts when specific planets will influence your life journey.', '2025-11-18 20:28:49.526825 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (6, 1, 5, 'ğŸŒŸ Beyond the 12 zodiac signs, Jyotish uses 27 "Nakshatras" or lunar mansions, offering incredibly precise details about personality and destiny.', '2025-11-18 20:28:49.526826 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (7, 1, 6, 'ğŸ’– Matching horoscopes (Kundali Milan) is a huge deal in Indian culture for marriages, with detailed systems like "Ashta Koota" ensuring compatibility.', '2025-11-18 20:28:49.526827 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (8, 1, 7, 'ğŸ’ Feeling a bit off-kilter? Jyotish offers "Upayas" or remedies like wearing specific gemstones, chanting mantras, or performing pujas to balance planetary energies.', '2025-11-18 20:28:49.526828 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (9, 1, 8, 'ğŸ“œ Your birth chart, or "Kundali," is considered a karmic blueprint, revealing past life influences and potential challenges or blessings in this lifetime.', '2025-11-18 20:28:49.526829 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (10, 1, 9, 'ğŸ  The 12 "Bhavas" or houses in your chart represent different life areas, from career and relationships to wealth and spirituality, like a cosmic floor plan.', '2025-11-18 20:28:49.526830 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (11, 1, 10, 'ğŸ§  Jupiter (Guru) is the "Great Benefic," bringing wisdom, luck, and expansion, while Saturn (Shani) is the "Taskmaster," teaching discipline and resilience through challenges.', '2025-11-18 20:28:49.526831 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (12, 1, 11, 'ğŸ•°ï¸ "Muhurta" is a fascinating branch of Jyotish that helps pinpoint the most auspicious time to start important events like weddings, new businesses, or housewarmings.', '2025-11-18 20:28:49.526832 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (13, 1, 12, 'ğŸ©º Jyotish has historical ties to Ayurveda, where planetary influences are considered in diagnosing and treating health conditions, linking cosmic energies to bodily well-being.', '2025-11-18 20:28:49.526834 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (14, 1, 13, 'ğŸ§© For deeper insights, Jyotish uses intricate "Varga" or divisional charts, like the Navamsha (D9) chart, which reveals secrets about marriage and inner self.', '2025-11-18 20:28:49.526835 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (15, 1, 14, 'ğŸ™ Despite showing tendencies, Jyotish emphasizes that personal effort (Purushartha) and free will are crucial; it''s a guide, not a fixed fate!', '2025-11-18 20:28:49.526836 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (16, 1, 15, 'âœ‹ Many practitioners of Jyotish also delve into Samudrika Shastra (Indian Palmistry), believing lines on the hand reflect planetary influences from the birth chart.', '2025-11-18 20:28:49.526837 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (17, 1, 16, 'ğŸ Rahu and Ketu, the north and south lunar nodes, are unique to Vedic astrology, representing our karmic desires and liberation points, respectively.', '2025-11-18 20:28:49.526838 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (18, 1, 17, 'ğŸ”„ Retrograde planets in your birth chart aren''t necessarily bad! They often indicate internalizing those planetary energies, leading to deep reflection and unique abilities.', '2025-11-18 20:28:49.526838 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (19, 1, 18, 'ğŸ•‰ï¸ Mantras are powerful sound vibrations used in Jyotish as a remedy. Chanting specific mantras can appease planets and harmonize their effects in your life.', '2025-11-18 20:28:49.526839 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (20, 1, 19, 'ğŸ“… The Indian calendar (Panchang) is intrinsically linked to Jyotish, guiding daily auspicious timings, festival dates, and agricultural activities based on lunar phases and planetary positions.', '2025-11-18 20:28:49.526840 +00:00');
