INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (480, 25, 0, 'ğŸ—¿ Skara Brae in Orkney is older than the Egyptian pyramids! This incredibly well-preserved Neolithic village offers a fascinating glimpse into life over 5,000 years ago, complete with stone beds and dressers. It''s like a time capsule! ğŸ¡', '2025-11-18 20:34:11.342628 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (481, 25, 1, 'ğŸ›¡ï¸ The Romans built the Antonine Wall across central Scotland, but it was occupied for only about 20 years! They kept retreating, suggesting the native Caledonian tribes were fierce warriors who gave the legions a run for their money. ğŸ’ª', '2025-11-18 20:34:11.342646 +00:00');
