INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (484, 25, 4, 'ğŸ›– Crannogs were amazing Iron Age homes built on artificial islands in lochs! These watery fortresses provided protection and a unique lakeside lifestyle for thousands of years. Imagine living on a floating house! ğŸ›¶', '2025-11-18 20:34:11.342649 +00:00');
