INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (490, 25, 10, 'ğŸ– Early Scots enjoyed a surprisingly diverse diet, with archaeological finds showing evidence of wild boar, deer, fish, shellfish, and early cultivated crops like barley and wheat. They were true foragers and farmers! ğŸŒ¾', '2025-11-18 20:34:11.342654 +00:00');
