INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (739, 38, 0, 'ğŸ™ Rajinikanth is often revered as a demigod by his fans in Tamil Nadu, with some building temples and performing pujas for his well-being. This devotion transcends typical fan adoration, bordering on spiritual worship.', '2025-11-18 20:37:13.429606 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (740, 38, 1, 'ğŸ—£ï¸ His "punch dialogues" are legendary, becoming part of everyday Tamil vocabulary and often quoted in political speeches, daily conversations, and even during cricket matches. They are more than just lines; they are cultural touchstones.', '2025-11-18 20:37:13.429616 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (741, 38, 2, 'âœ¨ The "Rajini style" â€“ his iconic walk, cigarette flick, and hair flip â€“ is instantly recognizable and widely imitated, making him a trendsetter whose unique mannerisms are celebrated globally. Even a simple movement becomes an event.', '2025-11-18 20:37:13.429617 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (742, 38, 3, 'ğŸ¬ Film release days for a Rajini movie are like major festivals in Tamil Nadu, with fans decorating theaters, performing milk ablutions (pal abhishekam) on his posters, and bursting firecrackers. It''s a carnival of devotion!', '2025-11-18 20:37:13.429618 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (743, 38, 4, 'ğŸ‘‘ He holds the undisputed title of "Superstar" in Tamil cinema, a moniker so powerful that no other actor dares to claim it. His presence alone guarantees a massive opening, cementing his unshakeable box office reign.', '2025-11-18 20:37:13.429619 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (744, 38, 5, 'ğŸ—³ï¸ For decades, there''s been constant speculation and fervent hope among fans for his entry into politics, proving that his influence extends far beyond cinema into the socio-political fabric of the state. His silence often speaks volumes.', '2025-11-18 20:37:13.429620 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (745, 38, 6, 'ğŸ‘¨â€ğŸ‘©â€ğŸ‘§â€ğŸ‘¦ Rajinikanth''s appeal cuts across all generations and demographics, from toddlers mimicking his actions to grandfathers admiring his enduring charisma. He truly is a hero for everyone.', '2025-11-18 20:37:13.429621 +00:00');
