INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (821, 42, 2, 'âœï¸ Quentin Tarantino is renowned for his verbose, often non-linear, and pop culture-infused dialogue that feels both real and heightened, like in *Pulp Fiction*. His unique voice is instantly recognizable.', '2025-11-18 20:38:13.887454 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (822, 42, 3, 'ğŸš¶â€â™‚ï¸ğŸ’¬ Aaron Sorkin is famous for his "walk and talk" dialogue, where characters deliver rapid-fire, intelligent, and often overlapping lines while moving through a scene, notably in *The Social Network*.', '2025-11-18 20:38:13.887455 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (823, 42, 4, 'â¤ï¸ Nora Ephron, writer of *When Harry Met Sally...*, crafted witty, heartfelt, and endlessly quotable romantic comedy dialogues that capture the nuances of relationships. "I''ll have what she''s having!" is legendary.', '2025-11-18 20:38:13.887456 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (824, 42, 5, 'âœ¨ "May the Force be with you," from *Star Wars* (written by George Lucas), transcended a simple farewell to become a cultural touchstone and an emblem of hope for generations.', '2025-11-18 20:38:13.887457 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (825, 42, 6, 'âœ‚ï¸ Many screenwriters advocate for minimal dialogue, believing "show, don''t tell" is paramount, and often cut lines during rewrites to let visuals and actions speak louder than words.', '2025-11-18 20:38:13.887458 +00:00');
