INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (827, 42, 8, 'ğŸ¤” The Coen Brothers (Joel and Ethan) are celebrated for their distinctive, often absurdist, darkly comedic, and philosophical dialogue that perfectly captures their eccentric characters and unique worlds.', '2025-11-18 20:38:13.887460 +00:00');
