INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (830, 42, 11, 'ğŸ¤– Arnold Schwarzenegger''s simple yet powerful line, "I''ll be back," from *The Terminator* (written by James Cameron and Gale Anne Hurd), became a global catchphrase for resilience.', '2025-11-18 20:38:13.887463 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (831, 42, 12, 'ğŸ­ Sometimes, a single, perfectly delivered word can encapsulate an entire film''s mystery, like "Rosebud" in Orson Welles and Herman J. Mankiewicz''s *Citizen Kane*, sparking a lifelong quest.', '2025-11-18 20:38:13.887463 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (832, 42, 13, 'ğŸ“ While TV often employs writers'' rooms, feature film screenwriting can be a more solitary craft, where a single writer or a small team meticulously polishes every line of dialogue.', '2025-11-18 20:38:13.887464 +00:00');
