INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (836, 42, 17, 'ğŸŒƒ Film noir often relied heavily on hard-boiled, cynical narration and quick, snappy dialogue to establish its dark, gritty atmosphere and define its morally ambiguous characters.', '2025-11-18 20:38:13.887468 +00:00');
