INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (939, 48, 0, 'ğŸ›‹ï¸ The term "binge-watching" became a household phrase thanks to OTT platforms, turning weekend plans into marathon sessions of our favorite shows.', '2025-11-18 20:39:39.954305 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (940, 48, 1, 'ğŸŒ OTT platforms shattered geographical barriers, allowing viewers in India to enjoy Korean dramas and Americans to discover European thrillers with ease, fostering a truly global content library.', '2025-11-18 20:39:39.954312 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (941, 48, 2, 'âœï¸ Web series often boast more creative freedom than traditional TV, pushing boundaries in storytelling, themes, and even episode length without strict network censors.', '2025-11-18 20:39:39.954313 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (942, 48, 3, 'ğŸ“Š Ever wonder how your next perfect show recommendation pops up? Sophisticated OTT algorithms analyze your viewing habits to suggest new content, sometimes eerily accurately!', '2025-11-18 20:39:39.954315 +00:00');
