INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (944, 48, 5, 'ğŸ“± The rise of web series created a "second screen" phenomenon, where viewers actively discuss episodes on social media in real-time, creating vibrant online communities.', '2025-11-18 20:39:39.954317 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (945, 48, 6, 'âš¡ï¸ Many web series cater to modern audiences with increasingly limited attention spans, offering shorter, punchier episode formats perfect for quick viewing sessions.', '2025-11-18 20:39:39.954318 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (946, 48, 7, 'ğŸš€ OTT cuts out the traditional broadcast middleman, allowing content creators and distributors to reach audiences directly, often leading to more niche and diverse programming.', '2025-11-18 20:39:39.954319 +00:00');
