INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (948, 48, 9, 'ğŸ“ˆ The COVID-19 pandemic dramatically accelerated OTT adoption, with millions worldwide flocking to streaming for entertainment and connection during periods of lockdown and isolation.', '2025-11-18 20:39:39.954321 +00:00');
