INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (952, 48, 13, 'ğŸ† Emmy and Golden Globe categories now regularly feature and award OTT original series, proving their critical acclaim and mainstream acceptance within the entertainment industry.', '2025-11-18 20:39:39.954325 +00:00');
