INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1503, 76, 4, 'ğŸ° Akbar built the magnificent city of Fatehpur Sikri, a UNESCO World Heritage site, only to abandon it after just 15 years, likely due to water scarcity. A beautiful ghost city! âœ¨', '2025-11-18 20:46:11.832108 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1504, 76, 5, 'ğŸ¨ Mughal miniature paintings are renowned for their intricate detail, vibrant colors, and fusion of Persian, Indian, and even European artistic styles. Each piece tells a story! ğŸ–¼ï¸', '2025-11-18 20:46:11.832109 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1505, 76, 6, 'â¤ï¸ Shah Jahan, the "Builder King," gifted the world the iconic Taj Mahal, a breathtaking mausoleum built for his beloved wife, Mumtaz Mahal. A true monument to love! ğŸ’', '2025-11-18 20:46:11.832110 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1506, 76, 7, 'ğŸ‘‘ Empress Nur Jahan, wife of Jahangir, was an incredibly powerful and influential woman who often ruled from behind the veil, minting coins in her name and even signing imperial decrees. A true trailblazer! ğŸ’ª', '2025-11-18 20:46:11.832111 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1507, 76, 8, 'ğŸŒ¸ Emperor Jahangir was a keen naturalist, ordering detailed paintings of flora and fauna, and recording observations in his autobiography, the *Tuzuk-i-Jahangiri*. He even had a pet crane! ğŸ¦¢', '2025-11-18 20:46:11.832112 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1508, 76, 9, 'ğŸ’° During its peak, the Mughal Empire was an economic powerhouse, famous for its rich trade in textiles, spices, and precious stones like the legendary Koh-i-Noor diamond. Imagine the wealth! ğŸ’', '2025-11-18 20:46:11.832113 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1509, 76, 10, 'â›°ï¸ Aurangzeb''s relentless and costly expansion into the Deccan Plateau, lasting nearly 27 years, severely overstretched the empire''s resources and treasury, contributing to its decline. A drain on power! ğŸ’¸', '2025-11-18 20:46:11.832114 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1510, 76, 11, 'âš”ï¸ The rise of the Maratha Empire under figures like Shivaji Maharaj posed a significant challenge to Mughal authority, using guerrilla warfare to carve out their own dominion. A fierce resistance! ğŸ', '2025-11-18 20:46:11.832114 +00:00');
