INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1514, 76, 15, 'ğŸ‡¬ğŸ‡§ The Battle of Plassey in 1757, though fought between the British East India Company and the Nawab of Bengal, marked a crucial turning point, paving the way for British colonial dominance in India. A game-changer! ğŸ—ºï¸', '2025-11-18 20:46:11.832119 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1610, 81, 12, 'ğŸš¢ The Company''s vast fleet and trade routes contributed significantly to the global maritime economy, connecting distant continents and cultures like never before. ğŸŒŠ', '2025-11-18 20:47:19.415776 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1515, 76, 16, 'ğŸ›ï¸ Mughal architecture wasn''t just about the Taj Mahal; it evolved from simpler red sandstone structures to intricate white marble marvels, blending Persian, Central Asian, and Indian styles beautifully. A feast for the eyes! âœ¨', '2025-11-18 20:46:11.832119 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1516, 76, 17, 'ğŸ—£ï¸ The Urdu language, a rich blend of Persian, Arabic, Turkish, and local Indian dialects, developed significantly during the Mughal era, becoming the *lingua franca* of the court and northern India. A lyrical tongue! âœï¸', '2025-11-18 20:46:11.832120 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1517, 76, 18, 'ğŸ˜¢ Bahadur Shah Zafar II, the last Mughal Emperor, was exiled by the British to Rangoon (Myanmar) after the Indian Rebellion of 1857, marking the official end of the once-mighty dynasty. A sad farewell! ğŸ’”', '2025-11-18 20:46:11.832121 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1518, 76, 19, 'ğŸ² The Mughals left an indelible mark on Indian cuisine, introducing rich, aromatic dishes like biryani, kebabs, and various curries that are still beloved worldwide today. A delicious heritage! ğŸŒ¶ï¸ğŸš', '2025-11-18 20:46:11.832122 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1519, 77, 0, 'ğŸ‘§ Born as Manikarnika Tambe, her childhood nickname was ''Manu.'' She was incredibly energetic and independent, even playing with boys and learning martial arts alongside them! ğŸ¤¸â€â™€ï¸', '2025-11-18 20:46:25.852367 +00:00');
