INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1580, 80, 2, 'ğŸ’° He introduced new coinage, a revised calendar, and a system of weights and measures, aiming to standardize and modernize Mysore''s economy.', '2025-11-18 20:47:06.834150 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1581, 80, 3, 'ğŸŒ± Tipu initiated significant land reforms, attempting to curtail the power of zamindars and directly benefit the peasantry by revising land revenue systems.', '2025-11-18 20:47:06.834154 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1582, 80, 4, 'ğŸ› Tipu Sultan is credited with establishing the sericulture (silk farming) industry in Mysore, which continues to be a major economic activity in the region today.', '2025-11-18 20:47:06.834158 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1583, 80, 5, 'ğŸŒ His astute diplomacy involved sending envoys to far-off lands like France, the Ottoman Empire, and Afghanistan, seeking strategic alliances against the British.', '2025-11-18 20:47:06.834161 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1584, 80, 6, 'ğŸ° His capital, Srirangapatna, was a formidable island fortress, a testament to his defensive strategies, with its impressive walls and well-planned infrastructure.', '2025-11-18 20:47:06.834164 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1585, 80, 7, 'ğŸŒ¸ Tipu contributed to the development of beautiful gardens, blending traditional Indian styles with Persian influences, enhancing the aesthetics of his capital.', '2025-11-18 20:47:06.834167 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1586, 80, 8, 'ğŸ“š Tipu possessed an extraordinary personal library, reportedly holding thousands of manuscripts on diverse subjects, showcasing his intellectual curiosity.', '2025-11-18 20:47:06.834170 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1587, 80, 9, 'ğŸ™ While a devout Muslim, Tipu also made endowments to several Hindu temples, including the famous Sringeri Sharada Peetham, reflecting complex religious policies.', '2025-11-18 20:47:06.834173 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1588, 80, 10, 'ğŸ§¸ His famed mechanical toy, "Tipu''s Tiger," depicting a tiger mauling a European soldier, is a macabre yet fascinating artifact now housed in London''s V&A Museum.', '2025-11-18 20:47:06.834176 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1589, 80, 11, 'ğŸ‡«ğŸ‡· Tipu''s alliance with Revolutionary France and his correspondence with Napoleon Bonaparte highlight his strategic vision to counter British dominance in India.', '2025-11-18 20:47:06.834179 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1590, 80, 12, 'âš“ He established a small but effective navy, complete with dockyards and a naval board, recognizing the importance of sea power for trade and defense.', '2025-11-18 20:47:06.834182 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1591, 80, 13, 'ğŸ›£ï¸ Tipu invested in public works, improving roads and constructing numerous tanks (reservoirs) to boost irrigation and agricultural productivity across his kingdom.', '2025-11-18 20:47:06.834185 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1592, 80, 14, 'ğŸ¥‹ Recognizing the importance of a strong military, he encouraged various martial arts and combat training among his soldiers, fostering a disciplined army.', '2025-11-18 20:47:06.834189 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1593, 80, 15, 'ğŸ—“ï¸ He introduced the Mauludi calendar, a unique lunisolar system with names for years and months derived from Arabic and Persian, showcasing administrative innovation.', '2025-11-18 20:47:06.834192 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1594, 80, 16, 'ğŸ”¬ Tipu had a keen interest in science and technology, commissioning advanced weaponry and mechanical devices, indicating a progressive mindset for his era.', '2025-11-18 20:47:06.834194 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1595, 80, 17, 'âœŠ Tipu Sultan remains a powerful symbol of anti-colonial resistance in India, revered for his steadfast opposition to British expansionism until his very last breath.', '2025-11-18 20:47:06.834198 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1596, 80, 18, 'ğŸ¤” Tipu''s legacy is often debated, with historians discussing his administrative reforms, religious policies, and treatment of conquered territories, making him a complex figure.', '2025-11-18 20:47:06.834200 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1597, 80, 19, 'ğŸ§µ He actively promoted the textile industry, encouraging the production of high-quality fabrics, including exquisite muslins, which were sought after globally.', '2025-11-18 20:47:06.834203 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1598, 81, 0, 'â˜• The EIC''s aggressive push for tea cultivation in India dramatically shifted global tea production from China, making India a world leader in the beverage we all enjoy today! ğŸŒ', '2025-11-18 20:47:19.415758 +00:00');
