INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1618, 82, 0, 'âš”ï¸ The Battle of Haldighati, though often considered a tactical Mughal victory, was a fierce stalemate fought in a narrow mountain pass, making it one of history''s most iconic unequal battles. Pratap''s forces, though vastly outnumbered, refused to yield easily.', '2025-11-18 20:47:34.081219 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1619, 82, 1, 'ğŸ Maharana Pratap''s legendary horse, Chetak, famously carried his injured master to safety by leaping across a wide chasm, sacrificing his own life in an ultimate act of loyalty. A true equine hero!', '2025-11-18 20:47:34.081239 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1620, 82, 2, 'ğŸŒ³ Pratap masterfully employed guerrilla warfare tactics, utilizing the dense forests and rugged Aravalli hills to launch surprise attacks and evade the larger Mughal army. This strategy kept the Mughals constantly on edge.', '2025-11-18 20:47:34.081240 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1621, 82, 3, 'ğŸ’° When Pratap faced severe financial crisis, his loyal minister Bhama Shah generously donated his entire wealth, enabling Pratap to raise a new army and continue his relentless resistance. A pivotal act of selfless patriotism!', '2025-11-18 20:47:34.081241 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1622, 82, 4, 'ğŸŒ¾ Legend recounts a poignant moment where Pratap''s family was forced to eat bread made from grass, almost breaking his resolve. This deep personal sacrifice highlighted his unwavering commitment to Mewar''s freedom.', '2025-11-18 20:47:34.081246 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1623, 82, 5, 'ğŸ‘‘ Emperor Akbar launched numerous campaigns over decades to subdue Pratap, viewing his continuous defiance as a personal challenge to the Mughal Empire''s absolute supremacy. Pratap was a formidable adversary!', '2025-11-18 20:47:34.081247 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1624, 82, 6, 'ğŸï¸ Pratap famously abandoned his capital city of Udaipur to the Mughals, preferring to live a harsh life in the jungles and hills rather than surrender or rule under Mughal suzerainty. Freedom was paramount.', '2025-11-18 20:47:34.081248 +00:00');
