INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1658, 84, 0, 'ğŸ—“ï¸ The Great Bengal Famine ravaged the region in 1943, occurring amidst the chaos of World War II, which heavily impacted relief efforts and the global perception of the crisis.', '2025-11-18 20:47:59.483052 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1659, 84, 1, 'ğŸŒ¾ Despite widespread starvation, British records confirm that massive amounts of rice and other grains were exported from India, including Bengal, for the war effort, even as millions died.', '2025-11-18 20:47:59.483070 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1660, 84, 2, 'ğŸ’” Estimates suggest around 3 million people perished during the famine, succumbing to starvation, malnutrition, and related diseases like cholera and malaria.', '2025-11-18 20:47:59.483071 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1661, 84, 3, 'ğŸ‡¬ğŸ‡§ British Prime Minister Winston Churchill is frequently criticized for his perceived indifference and alleged obstruction of aid, prioritizing British and Allied war needs over Bengali lives.', '2025-11-18 20:47:59.483073 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1662, 84, 4, 'â˜€ï¸ A devastating cyclone in October 1942, combined with tidal waves, destroyed a significant portion of the rice crop and fishing fleets, acting as a crucial catalyst for the famine.', '2025-11-18 20:47:59.483074 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1663, 84, 5, 'ğŸš« The British "denial policy," intended to thwart a potential Japanese invasion, involved confiscating boats and destroying rice stocks in coastal areas, crippling local food distribution.', '2025-11-18 20:47:59.483075 +00:00');
