INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (1665, 84, 7, 'ğŸ’° Profiteering and hoarding by local merchants and landlords, who inflated food prices exponentially, made essential food inaccessible to the poor and vulnerable.', '2025-11-18 20:47:59.483077 +00:00');
