INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2150, 108, 12, 'ğŸ“ˆ The success of Sindhu and the academy has dramatically boosted badminton''s popularity in India, inspiring countless young children to pick up a racket and dream big. It''s a true badminton boom! ğŸŒŸ', '2025-11-18 20:53:42.593893 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2151, 108, 13, 'ğŸ¦‰ Gopichand''s coaching philosophy emphasizes not just technique but also strategic thinking and adapting to opponents'' styles, turning his players into intelligent shuttlers. They learn to outsmart, not just outplay! ğŸ¤”', '2025-11-18 20:53:42.593894 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2152, 108, 14, 'ğŸ–ï¸ PV Sindhu has received multiple national honors, including the Padma Shri and Padma Bhushan, for her unparalleled achievements and for bringing glory to India on the global stage. A truly decorated athlete! âœ¨', '2025-11-18 20:53:42.593895 +00:00');
