INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2417, 122, 0, 'ğŸï¸ India first hosted a Formula 1 Grand Prix at the state-of-the-art Buddh International Circuit (BIC) in Greater Noida from 2011 to 2013, bringing the pinnacle of motorsport to Indian soil. The roar of the engines was electrifying!', '2025-11-18 20:57:07.597679 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2418, 122, 1, 'ğŸ‡®ğŸ‡³ Narain Karthikeyan became India''s first-ever Formula 1 driver in 2005, blazing a trail for aspiring racers and igniting national interest in the sport. He was a true speed pioneer!', '2025-11-18 20:57:07.597696 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2419, 122, 2, 'ğŸ›£ï¸ The Buddh International Circuit, designed by the legendary Hermann Tilke, boasts a thrilling 5.141 km track with a unique elevation change and demanding corners, making it a driver''s favorite. It''s a true test of skill!', '2025-11-18 20:57:07.597698 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2420, 122, 3, 'ğŸ† Sebastian Vettel of Red Bull Racing dominated the Indian Grand Prix, winning all three editions (2011, 2012, 2013) and even clinching his fourth World Championship title there in 2013. A hat-trick hero!', '2025-11-18 20:57:07.597700 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2421, 122, 4, 'ğŸ—£ï¸ The high-decibel thrill of F1 cars wasn''t just heard; it was felt by thousands of passionate Indian fans who flocked to the BIC, creating an unforgettable atmosphere of cheers and excitement. The crowd''s energy was palpable!', '2025-11-18 20:57:07.597701 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2422, 122, 5, 'ğŸ› ï¸ The Force India F1 team, though based in the UK, was Indian-owned for several years, providing a direct connection for fans and proudly flying the Indian flag high in the global racing arena. A truly national endeavor!', '2025-11-18 20:57:07.597703 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2423, 122, 6, 'ğŸŒ  Beyond F1, India nurtures young talent through series like the MRF Challenge and JK Tyre Racing Championship, which serve as crucial stepping stones for future motorsport stars. These are the nurseries of speed!', '2025-11-18 20:57:07.597704 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2424, 122, 7, 'ğŸ“Š The F1 Indian Grand Prix significantly boosted sports tourism and showcased India''s capability to host world-class international events, putting the country on the global motorsport map. A grand spectacle for the world!', '2025-11-18 20:57:07.597705 +00:00');
