INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2428, 122, 11, 'ğŸ Karun Chandhok, another pioneering Indian F1 driver, has continued to be a prominent voice in motorsport, serving as a commentator and ambassador, keeping the thrill accessible to fans nationwide. An ambassador of adrenaline!', '2025-11-18 20:57:07.597710 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2429, 122, 12, 'ğŸš€ The BIC''s long back straight (over 1.2 km) allowed F1 cars to reach blistering speeds exceeding 320 km/h, offering a breathtaking display of raw power and aerodynamic efficiency. Pure speed personified!', '2025-11-18 20:57:07.597710 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2430, 122, 13, 'ğŸ¤© Attending an F1 race in India was a sensory overload â€“ the smell of burning rubber, the sight of vibrant liveries, and the sheer G-forces experienced by drivers, creating a unique, thrilling spectacle. An assault on the senses!', '2025-11-18 20:57:07.597711 +00:00');
