INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2432, 122, 15, 'ğŸŒŸ Many Bollywood celebrities and prominent personalities graced the F1 Indian Grand Prix, adding a touch of glamour and wider appeal, making it a star-studded affair both on and off the track. Glamour meets velocity!', '2025-11-18 20:57:07.597713 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2433, 122, 16, 'ğŸ’° The economic impact of the F1 race included significant job creation and investment in hospitality and infrastructure around the BIC, leaving a lasting legacy even after the races concluded. More than just a race!', '2025-11-18 20:57:07.597714 +00:00');
