INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2577, 130, 0, 'ğŸ¡ PMAY''s grand vision was to achieve "Housing for All" by 2022, ensuring every eligible urban family could have a pucca (all-weather) house. It''s a massive endeavor to provide shelter across the nation!', '2025-11-18 20:58:57.386319 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2578, 130, 1, 'âœ¨ Launched in 2015, the Pradhan Mantri Awas Yojana (PMAY) is a flagship initiative, not just a housing scheme, but a dream builder for millions of Indian families. It''s truly transformative!', '2025-11-18 20:58:57.386334 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2579, 130, 2, 'ğŸ’° The Credit Linked Subsidy Scheme (CLSS) component under PMAY is like a financial superhero, offering interest rate subsidies on home loans. This significantly reduces the burden of EMIs for eligible beneficiaries!', '2025-11-18 20:58:57.386337 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2580, 130, 3, 'ğŸ‘©â€ğŸ‘§â€ğŸ‘¦ PMAY promotes women empowerment by mandating that the female head of the household must be the owner or co-owner of the house. This gives women a stronger stake in family assets!', '2025-11-18 20:58:57.386339 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2581, 130, 4, 'ğŸ—ï¸ PMAY encourages the use of innovative, green, and disaster-resistant technologies for construction. Think faster builds and eco-friendlier homes, making future-ready housing a reality!', '2025-11-18 20:58:57.386342 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2582, 130, 5, 'ğŸ˜ï¸ PMAY is structured with four main components, catering to diverse needs: In-Situ Slum Redevelopment, Credit Linked Subsidy Scheme, Affordable Housing in Partnership, and Beneficiary-Led Construction. A truly multi-faceted approach!', '2025-11-18 20:58:57.386344 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2583, 130, 6, 'ğŸ“Š The scheme primarily targets Economically Weaker Sections (EWS), Low Income Groups (LIG), and Middle Income Groups (MIG). It ensures financial assistance reaches those who need it most, making housing accessible!', '2025-11-18 20:58:57.386347 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2584, 130, 7, 'ğŸ› ï¸ For EWS and LIG beneficiaries, PMAY offers financial assistance for constructing a new house or enhancing an existing one. Itâ€™s like a helping hand for building or upgrading your very own dream home!', '2025-11-18 20:58:57.386349 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2585, 130, 8, 'ğŸ’¡ The "In-Situ Slum Redevelopment" component transforms existing slums into formal, habitable housing units, often without relocation. It''s about upgrading entire communities right where they stand!', '2025-11-18 20:58:57.386351 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2586, 130, 9, 'ğŸš€ Through its "Beneficiary-Led Construction" (BLC) component, PMAY directly assists individual eligible families who want to build a new house or make significant enhancements to their existing one on their own land. You''re the architect of your dream!', '2025-11-18 20:58:57.386353 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2587, 130, 10, 'ğŸ¤ PMAY champions collaboration! The "Affordable Housing in Partnership" (AHP) component encourages state governments and local bodies to partner with the private sector to build affordable homes. Teamwork makes the dream work!', '2025-11-18 20:58:57.386356 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2588, 130, 11, 'ğŸ–¥ï¸ Applying for PMAY is largely digitized, making the process more transparent and accessible for applicants across the country. No more endless queues, just a few clicks to start your home journey!', '2025-11-18 20:58:57.386358 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (2589, 130, 12, 'ğŸ“ˆ As of early 2023, PMAY (Urban) had sanctioned over 1.18 crore houses, with millions already completed and delivered. That''s a staggering number of new homes built across India! ğŸ ', '2025-11-18 20:58:57.386360 +00:00');
