INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (3042, 153, 7, 'ğŸ’ƒ Certain folk dances, particularly those with martial origins like the ''Chhau'' dance, incorporate mock sword fights and movements directly derived from traditional Indian fencing styles. It''s where combat meets choreography! ğŸ­', '2025-11-18 21:04:48.637828 +00:00');
INSERT INTO public.topic_contents (id, topic_id, content_index, content, created_at) VALUES (3043, 153, 8, 'ğŸ›¡ï¸ The ''Katar'', a unique push-dagger with an H-shaped handle, required a very specific close-quarters fighting technique, almost a stabbing fencing style, making it distinct from traditional swordplay. It''s a punch with a blade! ğŸ’ª', '2025-11-18 21:04:48.637829 +00:00');
